import 'dart:async';import 'package:flutter/material.dart';class SplashScreen extends StatefulWidget {  const SplashScreen({Key? key}) : super(key: key);  @override  State<SplashScreen> createState() => _SplashScreenState();}class _SplashScreenState extends State<SplashScreen> {  @override  void initState() {    super.initState();    Timer(      Duration(seconds: 3),      () => Navigator.pushNamed(        context,        '/LoginScreen',      ),    );  }  @override  Widget build(BuildContext context) {    return FlutterLogo();  }}