import 'dart:convert';import 'package:demo/Common/Common.dart';import 'package:demo/Model/Data.dart';import 'package:flutter/material.dart';import 'package:http/http.dart' as http;class HomeScreen extends StatefulWidget {  const HomeScreen({Key? key}) : super(key: key);  @override  State<HomeScreen> createState() => _HomeScreenState();}class _HomeScreenState extends State<HomeScreen> {  final username = TextEditingController();  final email = TextEditingController();  @override  Widget build(BuildContext context) {    return SafeArea(      child: Scaffold(        appBar: AppBar(          backgroundColor: Colors.white,          foregroundColor: Colors.black,        ),        drawer: Drawer(          child: Column(            children: [              SizedBox(height: 100),              TextCard(                controller: username,                icon: const Icon(Icons.person),                text: "Username",              ),              TextCard(                keybord: TextInputType.emailAddress,                controller: email,                icon: const Icon(Icons.email),                text: "Email",              ),            ],          ),        ),        body: FutureBuilder<Datamodel>(          future: ApiCalling(),          builder: (context, index) {            if (index.hasData) {              return const Center(                child: CircularProgressIndicator(),              );            }            Datamodel D = index.data ?? Datamodel();            return Padding(              padding: EdgeInsets.symmetric(horizontal: 100),              child: Column(                crossAxisAlignment: CrossAxisAlignment.center,                mainAxisAlignment: MainAxisAlignment.center,                children: [                  Text("${D.message}"),                  Text("${D.data}"),                  Text("${D.status}"),                  Text("${D.isSuccess}"),                ],              ),            );          },        ),      ),    );  }  Future<Datamodel> ApiCalling() async {    var url = Uri.parse(        "http://3.110.6.152:3100/v1/customers/testRegister?username=jagannath&emailId=jagannath007@gmail.com&password=ashvin123");    var res = await http.get(url);    var jsonResponse = jsonDecode(res.body);    return Datamodel.fromJson(jsonResponse);  }}